#!/usr/bin/env node
'use strict';

var pkg = require('./package.json');

var program = require('commander')
    .version(pkg.version);

/**
 * 脚手架相关
 */
program
    .command('init')
    .description('init a new module framwork in your current directory')
    .action(function () {
        console.log('init');
    });

program
    .command('publish <version>')
    .description('publish your module')
    .action(function (version) {
        console.log(version);
    });

program
    .command('build')
    .description('build your module')
    .action(function () {
        console.log('build');
    });

program.parse(process.argv);

// print help if there is no sub-command
if (!process.argv.slice(2).length) {
    program.outputHelp();
}
